import "./App.css";
import { useState } from "react";
import { upperCaseLetters, lowerCaseLetters, numbers, special } from "./data";

function App() {
  const [password, setPassword] = useState("");
  const [counter, setCounter] = useState(6);
  const [isUppercase, setIsUppercase] = useState(false);
  const [isLowercase, setIsLowercase] = useState(false);
  const [isNumbers, setIsNumbers] = useState(false);
  const [isSymbols, setIsSymbols] = useState(false);

  const increaseCounter = (e) => {
    e.preventDefault();

    if (counter < 20) {
      setCounter((prevCounter) => prevCounter + 1);
    }
  };

  const decreaseCounter = (e) => {
    e.preventDefault();

    if (counter > 6) {
      setCounter((prevCounter) => prevCounter - 1);
    }
  };

  const generatePassword = (e) => {
    e.preventDefault();

    let _password = "";

    for (let i = 0; i < counter; i++) {
      _password += getRandom();
    }

    setPassword(_password);
  };

  const getRandom = () => {
    const chars = [];

    if (isUppercase) {
      chars.push(
        upperCaseLetters[Math.floor(Math.random() * upperCaseLetters.length)]
      );
    }
    if (isLowercase) {
      chars.push(
        lowerCaseLetters[Math.floor(Math.random() * lowerCaseLetters.length)]
      );
    }
    if (isNumbers) {
      chars.push(numbers[Math.floor(Math.random() * numbers.length)]);
    }
    if (isSymbols) {
      chars.push(special[Math.floor(Math.random() * special.length)]);
    }

    return chars[Math.floor(Math.random() * chars.length)];
  };

  const createCopy = (e) => {
    const texxtAreaE1 = document.createElement("textarea");
    texxtAreaE1.innerText = password;
    document.body.appendChild(texxtAreaE1);
    texxtAreaE1.select();
    document.execCommand("copy");
  };

  return (
    <div className="App bg-slate-200 w-[450px] mx-auto p-10 rounded-lg my-[15vh]">
      <div className="generator">
        <h2 className="generator__title text-center text-2xl font-bold">
          Password Generator
        </h2>
        <h4 className="password text-center">{password}</h4>
      </div>

      <form className="generator__form">
        <div className="generator__form-controls flex flex-col">
          <div className="generator__form-control space-x-2">
            <input
              checked={isUppercase}
              onChange={(e) => setIsUppercase(e.target.checked)}
              type="checkbox"
              id="uppercase"
              name="uppercase"
            />
            <label htmlFor="uppercase">Uppercase</label>
          </div>
          <div className="generator__form-control space-x-2">
            <input
              checked={isLowercase}
              onChange={(e) => setIsLowercase(e.target.checked)}
              type="checkbox"
              id="lowercase"
              name="lowercase"
            />
            <label htmlFor="lowercase">Lowercase</label>
          </div>
          <div className="generator__form-control space-x-2">
            <input
              checked={isNumbers}
              onChange={(e) => setIsNumbers(e.target.checked)}
              type="checkbox"
              id="numbers"
              name="numbers"
            />
            <label htmlFor="numbers">Numbers</label>
          </div>
          <div className="generator__form-control space-x-2">
            <input
              checked={isSymbols}
              onChange={(e) => setIsSymbols(e.target.checked)}
              type="checkbox"
              id="uppercase"
              name="uppercase"
            />
            <label htmlFor="symbols">Symbols</label>
          </div>

          <div className="generator__length flex flex-col items-center space-y-2 bg-slate-500 mt-4 py-4 rounded-lg">
            <h4 className="generator__length-title text-center">
              Password Length
            </h4>
            <div className="generator__length-counter">
              <button
                onClick={decreaseCounter}
                className="py-1 px-4 bg-white rounded-lg"
              >
                -
              </button>
              <span className="px-4">{counter}</span>
              <button
                onClick={increaseCounter}
                className="py-1 px-4 bg-white rounded-lg"
              >
                +
              </button>
            </div>
          </div>

          <div className="generator__form-action flex flex-col justify-around pt-6 space-y-2">
            <button
              onClick={generatePassword}
              className="btn generate-btn py-2 rounded-lg text-white bg-[#2a2a2a]"
            >
              Generate Password
            </button>
            <button className="btn copy-btn py-2 rounded-lg text-[#2a2a2a] bg-slate-200 border border-[#2a2a2a]">
              Copy Password
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}

export default App;
